// Extract video filename from ComfyUI outputs
// The updated ComfyUI node now returns both images and videos
const items = $input.all();

// Find the first video output (fileType: 'video')
const videoItem = items.find(item => item.json.fileType === 'video');

if (!videoItem) {
  throw new Error('No video output found from ComfyUI workflow');
}

const filename = videoItem.json.filename;
const videoPath = `/mnt/raven-nas/inspirational-shorts/generated-content/${filename}`;

console.log(`Found video: ${filename}`);

return {
  json: {
    filename: filename,
    video_path: videoPath,
    subfolder: videoItem.json.subfolder || '',
    format: videoItem.json.format || 'video/mp4'
  }
};

// Prepare data for FFmpeg overlay with text wrapping
const quote = $('Parse Prompts').item.json.original_quote;
const videoPath = $input.item.json.video_path;
const inputFilename = $input.item.json.filename;

// Wrap text to fit within video width (720px - 200px padding = 520px max width)
// At 55px font size with more padding, approximately 20 characters per line is safe
const maxCharsPerLine = 20;
function wrapText(text, maxChars) {
  const words = text.split(' ');
  const lines = [];
  let currentLine = '';

  for (const word of words) {
    const testLine = currentLine ? `${currentLine} ${word}` : word;
    if (testLine.length <= maxChars) {
      currentLine = testLine;
    } else {
      if (currentLine) lines.push(currentLine);
      currentLine = word;
    }
  }
  if (currentLine) lines.push(currentLine);

  return lines.join('\\n');
}

const wrappedQuote = wrapText(quote, maxCharsPerLine);

// Generate paths
const timestamp = Date.now();
const textFilePath = `/tmp/quote_${timestamp}.txt`;
const outputFilename = inputFilename.replace(/\.mp4$/, '_overlay.mp4');
const outputPath = `/mnt/raven-nas/inspirational-shorts/generated-content/${outputFilename}`;

console.log('Input video:', videoPath);
console.log('Quote:', quote);
console.log('Wrapped quote:', wrappedQuote);
console.log('Text file:', textFilePath);
console.log('Output:', outputPath);

return {
  json: {
    quote: quote,
    wrapped_quote: wrappedQuote,
    text_file: textFilePath,
    input_video: videoPath,
    output_video: outputPath,
    output_filename: outputFilename
  }
};

// Pass through data from Prepare FFmpeg Data node
const prepareData = $('Prepare FFmpeg Data').item.json;

return {
  json: {
    input_video: prepareData.input_video,
    output_video: prepareData.output_video,
    text_file: prepareData.text_file,
    output_filename: prepareData.output_filename,
    quote: prepareData.quote
  }
};

// Return the final video with overlay
const outputFilename = $('Prepare FFmpeg Data').item.json.output_filename;
const outputPath = $('Prepare FFmpeg Data').item.json.output_video;

return {
  json: {
    filename: outputFilename,
    video_path: outputPath,
    original_filename: $('Extract Wan22 Video Filename').item.json.filename
  }
};

