// Find the most recent wan22 video in generated-content
// Using n8n's $fs helper which is available in the sandbox
const contentDir = '/mnt/raven-nas/inspirational-shorts/generated-content';

// List all files in the directory
const allFiles = await $fs.readdir(contentDir);

// Filter for wan22 videos and get their stats
const videoFiles = [];
for (const filename of allFiles) {
  if (filename.startsWith('wan22_') && filename.endsWith('.mp4')) {
    const fullPath = `${contentDir}/${filename}`;
    const stats = await $fs.stat(fullPath);
    videoFiles.push({
      name: filename,
      time: stats.mtimeMs,
      path: fullPath
    });
  }
}

// Sort by modification time (newest first)
videoFiles.sort((a, b) => b.time - a.time);

if (videoFiles.length === 0) {
  throw new Error('No wan22 video found in generated-content directory');
}

const newestVideo = videoFiles[0];

return {
  json: {
    filename: newestVideo.name,
    video_path: newestVideo.path
  }
};

