// Parse VRAM info from ComfyUI stats
const stats = $input.item.json;
const attempt = $('Initialize Retry Counter').item.json.vram_check_attempt;
const maxAttempts = $('Initialize Retry Counter').item.json.max_attempts;

// Get free VRAM from ComfyUI stats
let freeVramGB = 0;

// ComfyUI provides vram_free directly in devices array
if (stats.devices && stats.devices[0] && stats.devices[0].vram_free) {
  freeVramGB = stats.devices[0].vram_free / (1024 * 1024 * 1024);
} else if (stats.system && stats.system.vram_free) {
  freeVramGB = stats.system.vram_free / (1024 * 1024 * 1024);
}

// We need at least 25GB free for Ovi (it uses ~28GB but with CPU offload)
const requiredVramGB = 25;
const isReady = freeVramGB >= requiredVramGB;
const maxAttemptsReached = attempt >= maxAttempts;

// Log for debugging
console.log(`VRAM Check - Attempt ${attempt}/${maxAttempts}: Free=${freeVramGB.toFixed(2)}GB, Required=${requiredVramGB}GB, Ready=${isReady}`);

// Throw error if max attempts reached and still not ready
if (maxAttemptsReached && !isReady) {
  throw new Error(`VRAM timeout after ${attempt} attempts. Free: ${freeVramGB.toFixed(2)}GB, Required: ${requiredVramGB}GB. ComfyUI may still be holding memory.`);
}

return {
  json: {
    ...($('Initialize Retry Counter').item.json),
    vram_free_gb: parseFloat(freeVramGB.toFixed(2)),
    vram_required_gb: requiredVramGB,
    is_vram_ready: isReady,
    should_retry: !isReady && !maxAttemptsReached,
    should_proceed: isReady,
    debug_stats: {
      attempt: attempt,
      max_attempts: maxAttempts,
      devices_found: stats.devices ? stats.devices.length : 0,
      vram_free_bytes: stats.devices?.[0]?.vram_free || 0
    }
  }
};

// Initialize retry counter on first run
if (!$input.item.json.vram_check_attempt) {
  return {
    json: {
      ...($input.item.json),
      vram_check_attempt: 1,
      max_attempts: 10,
      vram_check_start: Date.now()
    }
  };
}

// Otherwise increment counter
return {
  json: {
    ...($input.item.json),
    vram_check_attempt: $input.item.json.vram_check_attempt + 1
  }
};

// Capture execution context and image info before waiting
const imageData = $('ComfyUI: Generate Image').item.json;
const imagePrompt = $('Parse Prompts').item.json.image_prompt;
const executionId = $execution.id;

// Log for debugging
console.log('Image Approval - Execution ID:', executionId);
console.log('Image filename:', imageData.filename);

return {
  json: {
    image_filename: imageData.filename,
    image_prompt: imagePrompt,
    execution_id: executionId,
    status: 'preparing_for_image_approval',
    resumeUrl: $execution.resumeUrl
  }
};

// Process the image approval response from the webhook
const webhookData = $input.item.json;
const action = webhookData.body.action;

// Get the original image prompt
const originalPrompt = $('Parse Prompts').item.json.image_prompt;

// Determine the final prompt and approval status
let finalPrompt = originalPrompt;
let approved = false;
let needsRegeneration = false;

if (action === 'approve') {
  approved = true;
  finalPrompt = originalPrompt;
} else if (action === 'edit' && webhookData.body.edited_image_prompt) {
  approved = true;
  needsRegeneration = true;
  finalPrompt = webhookData.body.edited_image_prompt;
} else if (action === 'reject') {
  approved = false;
}

return {
  json: {
    image_prompt: finalPrompt,
    approved: approved,
    needs_regeneration: needsRegeneration,
    action: action,
    original_image_prompt: originalPrompt
  }
};

// Parse the JSON output from the AI agent
const output = $input.item.json.output;
let prompts;

try {
  // Try to parse as JSON
  prompts = JSON.parse(output);
} catch (e) {
  // If not valid JSON, try to extract JSON from the text
  const jsonMatch = output.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    prompts = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Could not parse JSON from AI output');
  }
}

// Get the original quote from whichever path was taken
// Try Quote writer first (AI-generated), then Format Custom Quote (user-provided)
let originalQuote;
try {
  originalQuote = $('Quote writer').item.json.output;
} catch (e) {
  originalQuote = $('Format Custom Quote').item.json.output;
}

return {
  json: {
    image_prompt: prompts.image_prompt,
    video_prompt: prompts.video_prompt,
    original_quote: originalQuote
  }
};

// Capture execution context and quote before waiting
let originalQuote;
try {
  originalQuote = $('Quote writer').item.json.output;
} catch (e) {
  try {
    originalQuote = $('Format Custom Quote').item.json.output;
  } catch (e2) {
    originalQuote = 'No quote found';
  }
}

// Get the execution context
const executionId = $execution.id;
// Note: $execution.resumeUrl will have the full URL with UUID after Wait node starts
// For now, just pass the execution ID

// Log for debugging
console.log('Execution ID:', executionId);

return {
  json: {
    quote: originalQuote,
    execution_id: executionId,
    status: 'preparing_for_approval',
    resumeUrl: $execution.resumeUrl
  }
};

// Process the approval response from the webhook
const webhookData = $input.item.json;
const action = webhookData.body.action;  // Access from body

// Get the original quote from previous nodes
let originalQuote;
try {
  originalQuote = $('Quote writer').item.json.output;
} catch (e) {
  try {
    originalQuote = $('Format Custom Quote').item.json.output;
  } catch (e2) {
    throw new Error('Could not find original quote');
  }
}

// Determine the final quote based on action
let finalQuote = originalQuote;
let approved = false;

if (action === 'approve') {
  approved = true;
  finalQuote = originalQuote;
} else if (action === 'edit' && webhookData.body.approved_quote) {  // Also from body
  approved = true;
  finalQuote = webhookData.body.approved_quote;  // Also from body
} else if (action === 'reject') {
  approved = false;
}

return {
  json: {
    output: finalQuote,
    approved: approved,
    action: action,
    original_quote: originalQuote
  }
};

// Format custom quote to match Quote writer output structure
const customQuote = $('Webhook Trigger').first().json.body.custom_quote;

return {
  json: {
    output: customQuote
  }
};

