parameters.jsCode:
// Parse the JSON output from the AI agent
const output = $input.item.json.output;
let prompts;

try {
  // Try to parse as JSON
  prompts = JSON.parse(output);
} catch (e) {
  // If not valid JSON, try to extract JSON from the text
  const jsonMatch = output.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    prompts = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Could not parse JSON from AI output');
  }
}

return {
  json: {
    image_prompt: prompts.image_prompt,
    video_prompt: prompts.video_prompt,
    original_quote: $('Quote writer').item.json.output
  }
};

// Initialize retry counter on first run
if (!$input.item.json.vram_check_attempt) {
  return {
    json: {
      ...($input.item.json),
      vram_check_attempt: 1,
      max_attempts: 10,
      vram_check_start: Date.now()
    }
  };
}

// Otherwise increment counter
return {
  json: {
    ...($input.item.json),
    vram_check_attempt: $input.item.json.vram_check_attempt + 1
  }
};

// Parse VRAM info from ComfyUI stats
const stats = $input.item.json;
const attempt = $('Initialize Retry Counter').item.json.vram_check_attempt;
const maxAttempts = $('Initialize Retry Counter').item.json.max_attempts;

// Get free VRAM from ComfyUI stats
let freeVramGB = 0;

// ComfyUI provides vram_free directly in devices array
if (stats.devices && stats.devices[0] && stats.devices[0].vram_free) {
  freeVramGB = stats.devices[0].vram_free / (1024 * 1024 * 1024);
} else if (stats.system && stats.system.vram_free) {
  freeVramGB = stats.system.vram_free / (1024 * 1024 * 1024);
}

// We need at least 25GB free for Ovi (it uses ~28GB but with CPU offload)
const requiredVramGB = 25;
const isReady = freeVramGB >= requiredVramGB;
const maxAttemptsReached = attempt >= maxAttempts;

// Log for debugging
console.log(`VRAM Check - Attempt ${attempt}/${maxAttempts}: Free=${freeVramGB.toFixed(2)}GB, Required=${requiredVramGB}GB, Ready=${isReady}`);

// Throw error if max attempts reached and still not ready
if (maxAttemptsReached && !isReady) {
  throw new Error(`VRAM timeout after ${attempt} attempts. Free: ${freeVramGB.toFixed(2)}GB, Required: ${requiredVramGB}GB. ComfyUI may still be holding memory.`);
}

return {
  json: {
    ...($('Initialize Retry Counter').item.json),
    vram_free_gb: parseFloat(freeVramGB.toFixed(2)),
    vram_required_gb: requiredVramGB,
    is_vram_ready: isReady,
    should_retry: !isReady && !maxAttemptsReached,
    should_proceed: isReady,
    debug_stats: {
      attempt: attempt,
      max_attempts: maxAttempts,
      devices_found: stats.devices ? stats.devices.length : 0,
      vram_free_bytes: stats.devices?.[0]?.vram_free || 0
    }
  }
};
