// Validate and extract input parameters
const item = $input.first();
const body = item.json.body || item.json;

const script = body.script;
if (!script) {
  throw new Error('Missing required parameter: script');
}

if (script.length > 500) {
  throw new Error('Script too long (max 500 characters)');
}

// Reference audio can be:
// 1. task_id from Step 1 (yttools)
// 2. Binary data in the request
// 3. URL to audio file
const referenceTaskId = body.reference_task_id;
const referenceUrl = body.reference_url;
const hasBinary = item.binary && Object.keys(item.binary).length > 0;

if (!referenceTaskId && !referenceUrl && !hasBinary) {
  throw new Error('Missing reference audio: provide reference_task_id, reference_url, or upload audio file');
}

return [{
  json: {
    script: script,
    reference_task_id: referenceTaskId,
    reference_url: referenceUrl,
    has_binary: hasBinary,
    model: body.model || 'F5-TTS',
    output_format: body.output_format || 'mp3'
  },
  binary: item.binary
}];

// Prepare vibevoice API request
const item = $input.first();
const script = $('Validate Input').item.json.script;
const model = $('Validate Input').item.json.model;
const outputFormat = $('Validate Input').item.json.output_format;

// Check if we have binary data (reference audio)
const binary = item.binary;
if (!binary || Object.keys(binary).length === 0) {
  throw new Error('No reference audio available');
}

// Get the binary key (usually 'data' or 'file')
const binaryKey = Object.keys(binary)[0];
const audioData = binary[binaryKey];

return [{
  json: {
    text: script,
    model: model,
    output_format: outputFormat,
    reference_audio_key: binaryKey
  },
  binary: {
    reference_audio: audioData
  }
}];

// Prepare final response
const item = $input.first();
const script = $('Validate Input').item.json.script;
const binary = item.binary;

// Generate filename
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
const fileName = `cloned_voice_${timestamp}.mp3`;

return [{
  json: {
    success: true,
    message: 'Voice cloned and TTS generated successfully',
    file_name: fileName,
    script: script,
    model: $('Validate Input').item.json.model,
    output_format: $('Validate Input').item.json.output_format,
    generation_time: new Date().toISOString()
  },
  binary: binary
}];
