// Extract uploaded file data from webhook
const item = $input.first();
const binaryData = item.binary;

// n8n stores uploaded files in binary data with key matching the form field name
// The form field name is 'file', so we look for binaryData.file
let fileName = 'uploaded-video';
let fileKey = 'file';

// Find the binary data key (could be 'file', 'data', or auto-generated)
const binaryKeys = Object.keys(binaryData || {});
if (binaryKeys.length === 0) {
  throw new Error('No file uploaded');
}

fileKey = binaryKeys[0]; // Use the first binary key
const fileData = binaryData[fileKey];

if (!fileData) {
  throw new Error('No file data found in upload');
}

// Get original filename
fileName = fileData.fileName || 'uploaded-video';

// Sanitize filename to prevent directory traversal
const sanitizedFileName = fileName.replace(/[\/\\]/g, '_').replace(/[^a-zA-Z0-9._-]/g, '_');
const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0, -5);
const finalFileName = `${timestamp}_${sanitizedFileName}`;

return [{
  json: {
    fileName: finalFileName,
    originalName: fileName,
    uploadTime: new Date().toISOString(),
    binaryKey: fileKey
  },
  binary: {
    data: fileData
  }
}];
